// Custom Exception classclass InsufficientFundException extends Exception {    public InsufficientFundException(String message) {        super(message);    }}// Bank Account classclass BankAccount {    private double balance;    public BankAccount(double balance) {        this.balance = balance;    }    public void withdraw(double amount) throws InsufficientFundException {        if (amount > balance) {            throw new InsufficientFundException("Insufficient funds to withdraw");        }        balance -= amount;    }    public double getBalance() {        return balance;    }}// Banking Application classpublic class BankingApplication {    public static void main(String[] args) {        BankAccount account = new BankAccount(1000.0);        try {            account.withdraw(1500.0); // This will throw InsufficientFundException        } catch (InsufficientFundException e) {            System.out.println(e.getMessage());        }        System.out.println("Current Balance: " + account.getBalance());    }}